// Telegraph Database
// -------------------------------------------------- //

module.exports.setup = function(server) {

    var connection = {}
    ,   i = server.database
    ;

    switch(server.database.adapter){

    case "mongo": case "couch": case "redis":
        connection = require("./adapters/" + i.adapter).install(server); 
        break;
        
    default: 
        server.info("No recognized database adapter found, starting in static mode");
        connection = require("./adapters/static").install(server);
    }

    return connection;

};